//~~~~ redux toolkit ~~~~

import {configureStore} from '@reduxjs/toolkit'
// import counterReducer from './counterReducer';
import {counterSlice} from './counterSlice'

const store = configureStore({
    reducers:{
        counter: counterSlice,
    }
});
export default store;
