// import React,{useState} from 'react';
import {useDispatch, useSelector} from 'react-redux';
// import {INCREMENT,DECREMENT} from '../redux/action';
import {DBV_Action,IBV_Action, INC_Action} from '../redux/action'
import {increment,decrement} from '../redux/counterSlice'
export default function Counter(){

    const {count} = useSelector(state => state.counter);
    const dispatch = useDispatch();

    const HIR = () =>{
        //     I_Action(dispatch)
        dispatch(increment())
    }
    const HDR = () =>{
        //     D_Action(dispatch)
        dispatch(decrement())
    }
    const HIR_5 = () =>{
            IBV_Action(dispatch)
    }
    const HDR_5 = () =>{
            DBV_Action(dispatch)
    }
    const INC_INC = (vl) =>{
            INC_Action(vl,dispatch)
    }
    return(
        <div>
            <p>{count}</p>
            <button onClick={HIR}>Increment</button>
            <button onClick={HDR}>Decrement</button> 
            <br/>
            <button onClick={HIR_5}>Increment BY 5</button>
            <button onClick={HDR_5}>Decrement BY 5</button>
            <br/>
            <button onClick={()=>INC_INC(3)}>Increment BY 3</button>
        </div>
    )
}
